import React from "react";
import '../Assets/Styles/ServicesPageStyles.css'
import ServicesComponent from "../Components/ServicesComponent";
import { useSpring, animated } from '@react-spring/web';
import { useInView } from 'react-intersection-observer';
import website from '../Assets/Img/website.png'
import webApp from '../Assets/Img/webApp.png'
import DataBase from '../Assets/Img/DataBase.png'
function ServicesPage(){
    const { ref, inView } = useInView({
        triggerOnce: true, 
        threshold: 0.1, 
      });
    const left = useSpring({
        opacity: inView ? 1 : 0,
        transform: inView ? 'translateX(0px)' : 'translateX(20px)',
        config: { duration: 800 },
      });
    const right = useSpring({
        opacity: inView ? 1 : 0,
        transform: inView ? 'translateX(0px)' : 'translateX(-20px)',
        config: { duration: 800 },
      });

    return(
        <main className="main" id="services">
            <section className="services-container" style={right} ref={ref}>
                <h1>Looking for a service?</h1>
                <div className="ser-cont">
                    <ServicesComponent src={website}  title="WebSite" description="Looking for a website? I design and create web pages."></ServicesComponent>
                    <ServicesComponent src={webApp}  title="App-web" description="Need a web application? I develop web applications, from frontend to backend."></ServicesComponent>
                    <ServicesComponent src={DataBase}  title="Data-Base" description="Problems with your data? I can manage or create databases for whatever you need."></ServicesComponent>
                </div>
                <button className="wsp-btn">WhatsApp</button>
            </section>
        </main>
    );
}

export default ServicesPage;
