import React, { useState } from 'react';
import './App.css';
import InformationComponent from './Components/InformationComponent';
import PagesComponent from './Components/PagesComponent';

function App() {
  const [SelectedPage, setSelectedPage] = useState('');
  const appClass = selectedPage ? 'selected-style' : 'default-style';
  return (
    <div className={`app-container ${appClass}`}>
      <InformationComponent setSelectedPage={setSelectedPage}></InformationComponent>
      <PagesComponent selectedPage={SelectedPage}></PagesComponent>
    </div>
  );
}

export default App;
