import React from "react";
import '../App.css'
import '../Assets/Styles/DescriptionPageStyles.css'
import { useSpring, animated } from '@react-spring/web';
import { useInView } from 'react-intersection-observer';

function DescriptionPage(){
    const left = useSpring({
        opacity: 1,
        transform: 'translateX(0px)',
        from: { opacity: 0, transform: 'translateX(20px)' },
        config: { duration: 800 }
      });
    const rigth = useSpring({
        opacity: 1,
        transform: 'translateX(0px)',
        from: { opacity: 0, transform: 'translateX(-20px)' },
        config: { duration: 800 }
      });
    return(
        <main className="main" id="description">
            <section className="description-cont">
            <animated.h1 style={left}>I'm Felipe Padr√≥s</animated.h1>
            <animated.p style={rigth}>I am currently studying software development at UADE. Im from Buenos Aires, Argentina
                and i love play Rugby and write code.
            </animated.p>
            <animated.p style={left}>Wanna see my Projets??</animated.p>
            <animated.p style={rigth}>Well!! Slide or click this button to redirect</animated.p>
            <div className="btns-cont">
            <button className="btn">Projects</button>
            <button className="btn">Download CV</button>
            </div>
            </section>

        </main>
    );
}

export default DescriptionPage;
